->\n\n<!-- wp:woocommerce/checkout-order-summary-shipping-block -->\n<div class=\"wp-block-woocommerce-checkout-order-summary-shipping-block\"></div>\n<!-- /wp:woocommerce/checkout-order-summary-shipping-block -->\n\n<!-- wp:woocommerce/checkout-order-summary-taxes-block -->\n<div class=\"wp-block-woocommerce-checkout-order-summary-taxes-block\"></div>\n<!-- /wp:woocommerce/checkout-order-summary-taxes-block --></div>\n<!-- /wp:woocommerce/checkout-order-summary-totals-block --></div>\n<!-- /wp:woocommerce/checkout-order-summary-block -->\n\n<!-- wp:woocommerce-paypal-payments/checkout-paylater-messages {\"ppcpId\":\"ppcp-41d1256d-8504-4324-8389-96a4bf501643\"} /--></div>\n<!-- /wp:woocommerce/checkout-totals-block --></div>\n<!-- /wp:woocommerce/checkout -->', 'Finalizar compra', '', 'publish', 'closed', 'closed', '', 'finalizar-compra', '', '', '2025-02-24 16:20:19', '2025-02-24 15:20:19', '', 0, 'http://localhost/wordpress/finalizar-compra/', 0, 'page', '', 0),
